import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, Button, TouchableHighlight, TouchableOpacity, Image } from 'react-native';



export default function App() {
  const printButton = () => {
    console.log('按到了')
  }

  const [count, setCount] = useState(0)

  const [rank, setRank] = useState('')

  const [type, setType] = useState(true)

  const changeEvaluate = (num) => {
    setCount(count => count + num)
    if (type === true) {
      if (count + num < 100000) {
        setRank('加油加油，這只是一時的')
      }
      else if (count + num >= 100000 && count + num < 1000000) {
        setRank('不錯喔! 越來越好了')
      }
      else if (count + num >= 1000000 && count + num < 10000000) {
        setRank('我都開始羨慕你了呢')
      }
      else if (count + num >= 10000000 && count + num < 30000000) {
        setRank('以下是我的帳戶，歡迎大小額捐款')
      }
      else {
        setRank('你可能有點沉浸在裡面了，請暫時關閉這支App')
      }
    }
    else {
      if (count + num < 100000) {
        setRank('呵呵，這只是開始')
      }
      else if (count + num >= 100000 && count + num < 1000000) {
        setRank('不錯喔! 越來越多了呢')
      }
      else if (count + num >= 1000000 && count + num < 10000000) {
        setRank('我都開始羨慕你了呢')
      }
      else if (count + num >= 10000000 && count + num < 30000000) {
        setRank('先等等，冷靜冷靜')
      }
      else {
        setRank('好了好了，這位先生/女士，這不是真的，讓自己變好會更實在喔~')
      }
    }
  }
  if (type === true) {
    return (

      <View style={styles.container}>
        <Text style={{ fontSize: 25 }}>{rank}</Text>

        <Text style={styles.maintext}>你的存款餘額</Text>
        <Text>$ {count}</Text>

        <View style={styles.bottmContainer}>
          <TouchableOpacity style={styles.button} onPress={() => changeEvaluate(10000)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'https://tshop.r10s.com/fce/c8d/44b7/2c5c/c01d/d210/e7c8/11a6e9b92a0242ac110003.jpg?_ex=460x460' }}
            />
            <Text style={styles.buttonText}>+10000</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button} onPress={() => changeEvaluate(100000)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'https://tshop.r10s.com/fce/c8d/44b7/2c5c/c01d/d210/e7c8/11a6e9b92a0242ac110003.jpg?_ex=460x460' }}
            />
            <Text style={styles.buttonText}>+100000</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button} onPress={() => changeEvaluate(1000000)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'https://tshop.r10s.com/fce/c8d/44b7/2c5c/c01d/d210/e7c8/11a6e9b92a0242ac110003.jpg?_ex=460x460' }}
            />
            <Text style={styles.buttonText}>+1000000</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.bottmContainer}>
          <TouchableOpacity style={styles.button} onPress={() => setCount(0)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'https://res2.pubu.tw/news/2905/81216/BIW9TC_xl.jpg' }}
            />
            <Text style={styles.buttonText}>金錢如浮雲</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.bottmContainer}>
          <TouchableOpacity style={styles.button} onPress={() => setType(false)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'data:image/jpeg;base64,' }}
            />
            <Text style={styles.buttonText}>邪惡模式</Text>
          </TouchableOpacity>
        </View>

        <StatusBar style="auto" />
      </View>
    );
  }
  else {
    return (

      <View style={styles.container}>
        <Text style={{ fontSize: 25 }}>{rank}</Text>

        <Text style={styles.maintext}>你討厭的人的負債</Text>
        <Text>$ {count}</Text>

        <View style={styles.bottmContainer}>
          <TouchableOpacity style={styles.button} onPress={() => changeEvaluate(10000)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'data:image/jpeg;base64,' }}
            />
            <Text style={styles.buttonText}>+10000</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button} onPress={() => changeEvaluate(100000)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'data:image/jpeg;base64,' }}
            />
            <Text style={styles.buttonText}>+100000</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.button} onPress={() => changeEvaluate(1000000)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'data:image/jpeg;base64,' }}
            />
            <Text style={styles.buttonText}>+1000000</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.bottmContainer}>
          <TouchableOpacity style={styles.button} onPress={() => setCount(0)}>
            <Image
              style={{ width: 180, height: 100 }}
              source={{ uri: 'https://attach.setn.com/newsimages/2020/05/26/2575643-XXL.jpg' }}
            />
            <Text style={styles.buttonText}>良心發現</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.bottmContainer}>
          <TouchableOpacity style={styles.button} onPress={() => setType(true)}>
            <Image
              style={{ width: 100, height: 100 }}
              source={{ uri: 'https://www.epochweekly.com/uploads/pictures/6723/content_605-74-01.jpg' }}
            />
            <Text style={styles.buttonText}>善良模式</Text>
          </TouchableOpacity>
        </View>

        <StatusBar style="auto" />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  maintext: {
    fontSize: 60,
    textAlign: 'center'
  },
  bottmContainer: {
    //height: 60,
    flexDirection: 'row',
  },
  buttonText: {
    fontSize: 20,
    color: 'red',
    fontWeight: 'bold',
  },
  button: {
    padding: 10,
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
});
